mapper.select:
SELECT COUNT(0) FROM itsnow_hosts


        name LIKE #{keyword}
        OR address LIKE #{keyword}
        OR description LIKE #{keyword}

SELECT h.*,
        (SELECT COUNT(s.id) FROM itsnow_schemas s WHERE h.id = s.host_id) schemas_count,
        (SELECT COUNT(p.id) FROM itsnow_processes p WHERE h.id = p.host_id) processes_count
FROM itsnow_hosts h


        h.name LIKE #{keyword}
        OR h.address LIKE #{keyword}
        OR h.description LIKE #{keyword}



    ORDER BY #{pageRequest.sort}


    #{pageRequest.offset}
    , #{pageRequest.pageSize}
