mapper.select:
select g.id, g.sn, g.group_name, g.description, g.created_at, g.updated_at
from groups g
where g.sn=#{sn}

select g.id, g.sn, g.group_name, g.description, g.created_at, g.updated_at
from groups g
order by ${sort} ${dir} limit #{offset}, #{size}

select g.id, g.sn, g.group_name, g.description, g.created_at, g.updated_at
from groups g
where g.group_name like #{keyword} or g.sn like #{keyword}
order by ${sort} ${dir} limit #{offset}, #{size}
